INSERT INTO `categories` (`id`, `cat_name`, `cat_description`) VALUES
(21, 'Science', '<p>The intellectual and practical activity encompassing the systematic study of the structure and behavior of the physical and natural world through observation and experiment. <a href="https://www.google.com/search?q=what+is+science&amp;oq=what+is+science&amp;aqs=chrome..69i57j0l5.11055j0j7&amp;sourceid=chrome&amp;es_sm=122&amp;ie=UTF-8">more</a></p>'),
(22, 'Information Technology', '<p><strong>Information technology</strong> (IT) is the application of computers and telecommunications equipment to store, retrieve, transmit and manipulate data, often in the context of a business or other enterprise.</p>'),
(23, 'Mathematics', '<p><span class="hvr">(Mathematics)</span> (<em><span class="hvr">functioning</span> as <span class="hvr">singular</span></em>) a <span class="hvr">group</span> of <span class="hvr">related</span> <span class="hvr">sciences,</span> <span class="hvr">including</span> <span class="hvr">algebra,</span> <span class="hvr">geometry,</span> <span class="hvr">and</span> <span class="hvr">calculus,</span><span class="hvr">concerned</span> <span class="hvr">with</span> <span class="hvr">the</span> <span class="hvr">study</span> of <span class="hvr">number,</span> <span class="hvr">quantity,</span> <span class="hvr">shape,</span> <span class="hvr">and</span> <span class="hvr">space</span> <span class="hvr">and</span> <span class="hvr">their</span> <span class="hvr">interrelationships</span> by <span class="hvr">using</span> a <span class="hvr">specialized</span><span class="hvr">notation</span></p>'),
(24, 'Accounts', '<p><a href="http://www.businessdictionary.com/definition/financial-records.html">Financial records</a> of an organization that <a href="http://www.businessdictionary.com/definition/register.html">register</a> all <a href="http://www.businessdictionary.com/definition/financial-transaction.html">financial transactions</a>, and must be kept at its <a href="http://www.businessdictionary.com/definition/principal.html">principal</a> <a href="http://www.businessdictionary.com/definition/office.html">office</a> or <a href="http://www.businessdictionary.com/definition/place-of-business.html">place of business</a>. The purpose of these <a href="http://www.businessdictionary.com/definition/record.html">records</a> is to enable anyone to <a href="http://www.businessdictionary.com/definition/appraise.html">appraise</a> the <a href="http://www.businessdictionary.com/definition/organization.html">organization''s</a> <a href="http://www.businessdictionary.com/definition/current.html">current</a> <a href="http://www.businessdictionary.com/definition/financial-position.html">financial position</a> with <a href="http://www.businessdictionary.com/definition/reasonable.html">reasonable</a> <a href="http://www.businessdictionary.com/definition/accuracy.html">accuracy</a>. Firms present their <a href="http://www.businessdictionary.com/definition/annual-accounts.html">annual accounts</a> in two main <a href="http://www.businessdictionary.com/definition/part.html">parts</a>: the <a href="http://www.businessdictionary.com/definition/balance-sheet.html">balance sheet</a>, and the income statement (<a href="http://www.businessdictionary.com/definition/income-statement.html">profit and loss account</a>). The annual accounts of a registered or <a href="http://www.businessdictionary.com/definition/incorporation.html">incorporated</a> firm are<a href="http://www.businessdictionary.com/definition/required.html">required</a> by <a href="http://www.businessdictionary.com/definition/law.html">law</a> to <a href="http://www.businessdictionary.com/definition/disclosure.html">disclose</a> a certain <a href="http://www.businessdictionary.com/definition/amount.html">amount</a> of <a href="http://www.businessdictionary.com/definition/information.html">information</a>. And have to be <a href="http://www.businessdictionary.com/definition/certified.html">certified</a> by an external <a href="http://www.businessdictionary.com/definition/auditor.html">auditor</a> that they present a ''<a href="http://www.businessdictionary.com/definition/true-and-fair-view.html">true and fair view</a>'' of the firm''s <a href="http://www.businessdictionary.com/definition/financial.html">financial</a> affairs.<br /><br />Read more: <a href="http://www.businessdictionary.com/definition/accounts.html#ixzz3yGemwgHX">http://www.businessdictionary.com/definition/accounts.html#ixzz3yGemwgHX</a></p>');
